import React, { useState } from "react";
import BotCard from "./BotCard";
import BotSpecs from "./BotSpecs";

function BotCollection({ bots, onEnlist, onDischarge }) {
  const [selectedBot, setSelectedBot] = useState(null);
  const handleBotClick = (bot) => {
    setSelectedBot(bot);
  };
  function handleBotDischarge(bot) {
    onDischarge(bot);
  }

  if (selectedBot) {
    return (
      <BotSpecs
        bot={selectedBot}
        enlistBot={onEnlist}
        navigateBack={handleGoBack}
      />
    );
  }

  function handleGoBack() {
    setSelectedBot(null);
  }

  const botCards = bots.map((bot) => (
    <BotCard
      key={bot.id}
      bot={bot}
      botFunction={handleBotClick}
      onDischargeBot={handleBotDischarge}
    />
  ));

  return (
    <>
      <h1 style={{ textAlign: "center" }}>Collection of all bots</h1>
      <div className="ui four column grid">
        <div className="row">{botCards}</div>
      </div>
    </>
  );
}

export default BotCollection;
